/*
ok. appending binary data to image does not work on iis.

encrypting the content with aes is not suitable as well, for the metadata length is limited as well

basically the metadata can be no more than a hash

*/
class CONTENT{
    private static $cipher = 'aes-256-cbc';
    private static $options = OPENSSL_RAW_DATA;
    private static $iv = "0123456789ABCDEF0123456789ABCDEF";

    public static function obfuscate($data){
        if (!$data) return null;
        $key = substr(hash('sha256', $data), 0, 32);
        $encrypted = openssl_encrypt($data, self::$cipher, $key, self::$options, hex2bin(self::$iv));
        return base64_encode($encrypted);
    }
    public static function verify($encrypted, $expected){
        if (!$encrypted || !$expected) return null;
        $key = substr(hash('sha256', $expected), 0, 32);
        return openssl_decrypt(base64_decode($encrypted), self::$cipher, $key, self::$options, hex2bin(self::$iv));
    }
}

